import React from 'react';
import { Routes, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Sidebar from './components/Menu';
import Header from './components/Header';
import Footer from './components/Footer';
import About from './components/About';
import Gallery from './components/Gallery';
import News from './components/News';
import Contacts from './components/Contacts';

function Slider() {
  return (
    <div className="slider p-3 bg-info text-white text-center">
      <h4>Latest News</h4>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {Array.from({ length: 10 }).map((_, i) => (
          <li key={i}>News {i + 1}</li>
        ))}
      </ul>
    </div>
  );
}

function HomePage() {
  return (
    <div className="content p-3">
      <h1>Welcome to the HR Department Portal</h1>
      <p>This portal provides information about the Human Resources Department of our institution.</p>
    </div>
  );
}

function App() {
  return (
    <div className="container-fluid">
      <Header />
      <div className="row">
        <div className="col-md-2">
          <Sidebar />
        </div>
        <div className="col-md-10">
          <Slider />
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/about" element={<About />} />
            <Route path="/gallery" element={<Gallery />} />
            <Route path="/news" element={<News />} />
            <Route path="/contact" element={<Contacts />} />
          </Routes>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
