import React, { useEffect, useState } from 'react';

function Gallery() {
  const [images, setImages] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    const imageCount = 100;
    const loadedImages = [];

    for (let i = 1; i <= imageCount; i++) {
      const img = new Image();
      img.src = `${process.env.PUBLIC_URL}/assets/pictures/image${i}.jpg`;
      img.onload = () => setImages(prev => [...prev, img.src]);
    }
  }, []);

  const extractTitle = (src) => {
    const parts = src.split('/').pop().split('.');
    return parts.slice(0, -1).join('.');
  };

  return (
    <div style={{ backgroundColor: 'rgba(255,255,255,0.75)', padding: '1rem', borderRadius: '8px' }}>
      <h2>Gallery</h2>
      {images.length === 0 ? (
        <p>No images uploaded yet. Place them in <code>/public/assets/pictures/</code></p>
      ) : (
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
          {images.map((src, i) => (
            <img
              key={i}
              src={src}
              alt={`Gallery ${i}`}
              style={{ width: '200px', height: '150px', objectFit: 'cover', cursor: 'pointer', borderRadius: '6px' }}
              onClick={() => setSelectedImage(src)}
            />
          ))}
        </div>
      )}

      {/* Expanded View */}
      {selectedImage && (
        <div
          onClick={() => setSelectedImage(null)}
          style={{
            position: 'fixed',
            top: 0, left: 0, right: 0, bottom: 0,
            backgroundColor: 'rgba(0,0,0,0.75)',
            zIndex: 9999,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            flexDirection: 'column',
            cursor: 'pointer'
          }}
        >
          <img src={selectedImage} alt="Expanded" style={{ maxWidth: '90%', maxHeight: '80vh', borderRadius: '8px' }} />
          <h4 style={{ color: 'white', marginTop: '1rem' }}>{extractTitle(selectedImage)}</h4>
        </div>
      )}
    </div>
  );
}

export default Gallery;
